function Home({data}){
    return (
        <div>
            <h1>Minhas Metas!</h1>
            {
                data.metas.map(meta => (
                    <div key={meta._id}>
                        <h3>{meta.name}</h3>
                        <p>{meta.description}</p>
                        <p>Meta: {meta.status}</p>
                    </div>
                ))}
        </div>
    )
}

export async function getServerSideProps() {
    const response = await fetch(`http://localhost:8080/metas`);
    const data = await response.json();
    //console.log(data);
    return { props: { data } };
}

export default Home;
